widgets.onCreate:
//from here:
//https://openstagecontrol.discourse.group/t/preform-fade-using-a-button/2082/5

globals.fade = function(btnMode, btnValue, wdgtToSet, from, to, dur) {
    var fps = 30
    if(btnMode == 'push') {
      if (btnValue) {
        // start fade when button is on
        var start = Date.now()
        setInterval(function(){
          var pos = (Date.now() - start) / dur
          if (pos >= 1) {
            clearInterval()
            pos = 1
          }
          var val = pos * (to - from) + from
          //send('midi:port', '/control', channel, cc, val)
          set(wdgtToSet, val)
        }, 1000 / fps)
      } else {
        // cancel when button is off ?
        clearInterval()
      }
    }

    else if(btnMode == 'toggle') {
      var start = Date.now()
        setInterval(function(){
          var pos = (Date.now() - start) / dur
          if (pos >= 1) {
            clearInterval()
            pos = 1
          }
          var val = pos * (to - from) + from
          //send('midi:port', '/control', channel, cc, val)
          set(wdgtToSet, val)
        }, 1000 / fps)
    }
}

