  //props.address = "/vimix/@_{addrPref}" + ($) + "/alpha"
  props.address = "/vimix/"+ addrRef + ($) + "/" + ccFeature
} else if (addrRef == 'byName') {
  props.address = "/vimix/"+@{addrRefByName} + "/" + ccFeature
  } else {
  props.address = "/vimix/" + addrRef + "/" + ccFeature
}

