  props.range = {"min":0.0,"max":1.0}
}
else if(ccFeature == "posterize"){
  props.range = {"min":0.0,"max":256.0}
} 
else if(ccFeature == "invert"){
  props.range = {"min":0.0,"max":2.0}
} 
else{
  props.range = {"min":-1.0,"max":1.0}
}
props.default = 0.0
props.origin = 0.0
props.doubleTap = true

props.onValue = `
  if(get('addrRefVal') != "batch#"){
    var idx = getIndex('this')
    if(get('locUpdateBtn_SETT')) set('selector', {"id": idx})
  }
`


return props
}

