widgets.props:
JS{
  var props = {}

  var labs = ['🠔', '🠕', '🠗', '🠖']
  props.label = labs[$]
  props.mode = "push"

  props.onValue = `

    if(value == 1 ){
      var idx = getIndex('this')
      var corner = get('cornerChoice_DEF')
      var curPoints = get('cornerMovePad_DEF')
      var prevs = [
        scale(curPoints[0],0,0.5, 0, 1),
        scale(curPoints[1],0,0.5, 0, 1),
        scale(curPoints[2],0,0.5, 0, 1),
        scale(curPoints[3],0.5,1,-1, 0),
        scale(curPoints[4],0.5,1, -1, 0),
        scale(curPoints[5],0,0.5, 0, 1),
        scale(curPoints[6],0.5,1, -1, 0),
        scale(curPoints[7],0.5,1,-1, 0)
        ]

      // mapping function
      function scale(number, inMin, inMax, outMin, outMax) {
          return (number - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
      }

      //move function
      function move(corn, dir) {
        var inc = (get('cornerInc_DEF')/1000)
        var newSetArr = []
        var newSendArr = []

        if(dir == 'up' || dir == 'down') corn = (corn+1)
        if(dir == 'left' || dir == 'down') inc = -inc

        for(var i=0; i<prevs.length; i++){
          //new vals for PAD 
          var valSet
          var valSend
          if(i == corn) {
            valSet = curPoints[i]+inc
            valSend = prevs[i]+inc
          }
          else {
            valSet = curPoints[i]
            valSend = prevs[i]
          }
          newSetArr.push(valSet)
          newSendArr.push(valSend)
        }
        set('cornerMovePad_DEF', newSetArr)
        send(get('oscTarget'), get('addrVarGeo') + '/corner' , newSendArr)

        //
      }

      //check index and movce accordingly
      if(idx == 0){
        move(corner ,'left')
      }
      else if(idx == 1){
        move(corner ,'up')
      }
      else if(idx == 2){
        move(corner ,'down')
      }
      else if(idx == 3){
        move(corner ,'right')
      }
    }

  `


  return props
}

