  var mappedVal = scale(value, -1, 1, 0, 3)
  send(get('oscTarget'),  "/vimix/current/"+addrSfx[idx], mappedVal)
}
else {
  send(get('oscTarget'),  "/vimix/current/"+addrSfx[idx], value)
}
`


return props
}

