  // get list into variable

  if(get('filCuesQntVar3') < 14) {
    var cueData = get('filterCueData')
    var idf = getIndex('this')+1
    var toDel = "filCue" + idf
    delete cueData[toDel]
    //console.log(Object.keys(cueData)[0])

    //rename all objects to ascending index: "filCue"+indexNumber
    // https://stackoverflow.com/questions/4647817/javascript-object-rename-key
    //https://bobbyhadz.com/blog/javascript-get-value-of-object-by-index
    for (var i=0; i<Object.keys(cueData).length; i++){
      var oldName = Object.keys(cueData)[i]
      var newName = "filCue"+(i+1)
      Object.defineProperty(cueData, newName, Object.getOwnPropertyDescriptor(cueData, Object.keys(cueData)[i]));
      //delete cueData[Object.keys(cueData)[i]];
      //console.log(Object.keys(cueData)[i])

    }

    set('filterCueData', cueData)
    console.log(toDel + ' DELETED!')
    console.log(get('filterCueData'))
  }

  if(get('filCuesQntVar3') > 0 && get('filCuesQntVar3') <= 15) {
  set('filCuesQntVar3', get('filCuesQntVar3') - 1)
